, useremail


, usergender


, category

)
values(carr_mem_id_seq.nextval, #{userid},#{userpwd},#{username},#{usernickname},#{useraddress},#{isbusiness},#{uuid},#{uploadPath},#{fileName}

, #{useremail}


, #{usergender}


, #{category}

)

mapper.update:
update carrot_member
set usernickname = #{usernickname}
, usergender = #{usergender}
, useremail = #{useremail}
, useraddress = #{useraddress}
, isbusiness = #{isbusiness}
where id = #{id}

update carrot_member
set userpwd = #{userpwd}
where id = #{id}
